/**
 * 检测 `value` 是否为 FormData 类型
 *
 * @param {*} value 待检测的值
 * @returns {boolean} 若 `value` 为 FormData 类型返回 `true`，否则返回 `false`
 */
export function isFormData(value): boolean;
